const nav=document.querySelector("nav"),navDropdownMenu=document.querySelectorAll(".nav-dropdown-menu");navDropdownMenu.forEach(navDropdownMenu=>{document.addEventListener("click",e=>{nav.contains(e.target)||navDropdownMenu.removeAttribute("open")})});const details=document.querySelectorAll("details");details.forEach(targetDetail=>{targetDetail.addEventListener("click",()=>{details.forEach(detail=>{detail!==targetDetail&&detail.removeAttribute("open")})})}),document.title="New NavBar (Beta)";